'/* Formatted on 11/22/2021 12:31:55 PM (QP5 v5.313) */',
'DECLARE',
'    vReqNo          NUMBER;',
'    vStatus         VARCHAR2 (10);',
'    vErrorFlag      VARCHAR2 (10);',
'    vErrorMessage   VARCHAR2 (1024);',
'    vStock          NUMBER;',
'    vItemName       VARCHAR2 (100);',
'',
'    CURSOR cReqDtlData',
'    IS',
'        SELECT AUTH_AMOUNT, REQ_ITEM_ID',
'          FROM INVENTORY.INV_REQ_DTL',
'         WHERE REQ_NO = :REQ_NO;',
'BEGIN',
'    vReqNo := :REQ_NO;',
'    vStatus := :REQ_FLAG;',
'',
'    IF :APEX$ROW_STATUS = ''U''',
'    THEN',
'        BEGIN',
'            FOR i IN cReqDtlData',
'            LOOP',
'                BEGIN',
'                    SELECT STOCK, ITEM_NAME',
'                      INTO vStock, vItemName',
'                      FROM INVENTORY.STOCK_REPORT',
'                     WHERE ITEM_ID = i.REQ_ITEM_ID;',
'                EXCEPTION',
'                    WHEN OTHERS',
'                    THEN',
'                        RAISE_APPLICATION_ERROR (-20001,',
'                                                 ''Stock Finding Problem.'');',
'                END;',
'',
'                IF i.AUTH_AMOUNT > vStock',
'                THEN',
'                    RAISE_APPLICATION_ERROR (',
'                        -20001,',
'                           ''For Courier No:''',
'                        || vReqNo',
'                        || '' ''',
'                        || '',Item :''',
'                        || vItemName',
'                        || '' is Out of Stock.'');',
'                END IF;',
'            END LOOP;',
'        END;',
'',
'',
'        BEGIN',
'            INVENTORY.PKG_INV_MAIN.PRC_INV_COURIER_SEND (',
'                pReqNo          => vReqNo,',
'                pCourierFlag    => vStatus,',
'                pRunUser        => :APP_USER,',
'                pErrorFlag      => vErrorFlag,',
'                pErrorMessage   => vErrorMessage);',
'        EXCEPTION',
'            WHEN OTHERS',
'            THEN',
'                RAISE_APPLICATION_ERROR (-20001, '' Authentication Problem.'');',
'        END;',
'',
'        IF NVL (vErrorFlag, ''N'') = ''Y''',
'        THEN',
'            RAISE_APPLICATION_ERROR (-20001, vErrorMessage || ''-'' || SQLERRM);',
'        END IF;',
'    END IF;',
'END;'))
,p_attribute_05=>'Y'
,p_attribute_06=>'Y'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_success_message=>'Send to Courier Successfully.'
);
