'                    1',
'              END',
'       AND 1 =',
'              CASE',
'                 WHEN (SELECT REGION',
'                         FROM GUMS.MB_USER_MST C',
'                        WHERE C.USER_CODE = A.CREATE_BY) =',
'                         (SELECT REGION',
'                            FROM GUMS.MB_USER_MST C',
'                           WHERE C.USER_CODE = :APP_USER)',
'                 THEN',
'                    1',
'              ',
'              WHEN (SELECT AREA',
'                         FROM GUMS.MB_USER_MST C',
'                        WHERE C.USER_CODE = A.CREATE_BY) =',
'                         (SELECT AREA',
'                            FROM GUMS.MB_USER_MST C',
'                           WHERE C.USER_CODE = :APP_USER)',
'                 THEN',
'                    1',
'                    ',
'                    WHEN (SELECT TERRITORY',
'                         FROM GUMS.MB_USER_MST C',
'                        WHERE C.USER_CODE = A.CREATE_BY) =',
'                         (SELECT TERRITORY',
