'SELECT TERITORY_CODE,',
'       TERITORY_NAME,',
'       DESCRIPTION,',
'       TARGET_VALUE / ctteri           TARGET_VALUE,',
'       PREV_TARGET_VALUE / ctteri      PREVIOUS_TARGET_VALUE,',
