'               PREV_ACHIEVEMENT_VALUE / CTREG',
'                   PREV_ACHIEVEMENT_VALUE_SPREAD,',
'               p.region_code,',
'               p.area_code,',
'               q.TERITORY_CODE,',
'               q.TERITORY_NAME,',
'               (SELECT COUNT (TERITORY_CODE)',
'                  FROM EMOB.TERITORY_INFO',
'                 WHERE     REGION_CODE = :P60_REGION',
'                       AND AREA_CODE = :P60_AREA_CODE',
'                       AND TERITORY_CODE =',
'                           DECODE (:P60_TERRITORY_CODE, 0, TERITORY_CODE, :P60_TERRITORY_CODE))',
'                   ctteri',
'          FROM (  SELECT b.REGION_name,',
'                         c.KPI_SUB_HEAD_DESC,',
'                         ACHIEVEMENT_VALUE,',
'                         (SELECT ACHIEVEMENT_VALUE',
'                            FROM INVENTORY.PM_YEARLY_KPI_TARGET_ACHIEVEMENT',
'                           WHERE     KPI_SUB_HEAD_ID = a.KPI_SUB_HEAD_ID',
'                                 AND REGION_ID = a.REGION_ID',
'                                 AND YEAR_CODE =',
'                                     SUBSTR (TO_CHAR (SYSDATE, ''YYYY''), 0) - 1)',
'                             prev_ACHIEVEMENT_VALUE,',
'                         GROWTH,',
'                         (SELECT GROWTH',
'                            FROM INVENTORY.PM_YEARLY_KPI_TARGET_ACHIEVEMENT',
'                           WHERE     KPI_SUB_HEAD_ID = a.KPI_SUB_HEAD_ID',
'                                 AND REGION_ID = a.REGION_ID',
'                                 AND YEAR_CODE =',
'                                     SUBSTR (TO_CHAR (SYSDATE, ''YYYY''), 0) - 1)',
