'                             prev_TARGET_VALUE,',
'                         AREA_SHORT_NAME,',
'                         area_code,',
'                         d.REGION_CODE,',
'                         (SELECT COUNT (AREA_CODE)',
'                            FROM EMOB.AREA_INFO',
'                           WHERE     REGION_CODE = :P60_REGION',
'                                 AND AREA_CODE =',
'                                     DECODE (:P60_AREA_CODE, 0, AREA_CODE, :P60_AREA_CODE))',
'                             CTREG           --,SUM(COUNT(AREA_CODE))AREA_CODE',
'                    FROM INVENTORY.PM_YEARLY_KPI_TARGET_ACHIEVEMENT a,',
'                         EMOB.REGION_INFO                          b,',
'                         INVENTORY.PM_KPI_SUB_HEAD                 c,',
'                         EMOB.AREA_INFO                            D',
'                   WHERE     A.YEAR_CODE =',
'                             SUBSTR (TO_CHAR (SYSDATE, ''YYYY''), 0)',
'                         AND REGION_ID = :P60_REGION',
'                         --AND A.KPI_SUB_HEAD_ID = 2',
'                         AND a.REGION_ID = b.REGION_CODE',
